const Header = (props) => {
  return(
    <h1>
      {props.course}
    </h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part str={`${props.p1['name']} ${props.p1['exercises']}`} />

    </div>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.str}
    </p>
  )
}

const Total = (props) => {
  return (
    <p>Number of exercises {props.ex1 + props.ex2 + props.ex3}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <>
      <Header course={course} />
      <Content p1={part1} p2={part2} p3={part3}/>
      <Total ex1={part1['exercises']} part2={'exercises'} part3={'exercises'}/>
    </>
  )
}

export default App
