import { useState } from 'react'

const Feedback = (props) => {

  return (
    <>
      <h1> give feedback</h1>
    </>
  )
}

const Statistics = ({good, bad, neutral, average}) => {
  statisticsHtml = `
  <div>
    <div>good {good}</div>
    <div>neutral {neutral}</div>
    <div>bad {bad}</div>
    <div>all {good+bad+neutral}</div>
    <div>average {average}</div>
    <div>positive {good/(good+bad+neutral)}</div>
  </div>
  `
  return (
    <>
      <h1>Statistics</h1>

    </>
  )
}

const Button = (props) => {

  return (
    <>
      <button onClick={props.handleClick}>{props.text}</button>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [average, setAverage] = useState(0)

  const handleGood = (newValue) => {
    setGood(newValue)
    calcAverage(newValue, bad, neutral)
  }
  const handleNeutral = (newValue) => {
    setNeutral(newValue)
    calcAverage(good, bad, newValue)
  }
  const handleBad = (newValue) => {
    setBad(newValue)
    calcAverage(good, newValue, neutral)
  }
  const calcAverage = (g, b, n) => {
    const m = (g-b) / (g+n+b)
    setAverage(m)
  }


  return (
    <div>
      <h1>give feedback</h1>
      <Button text='good' handleClick={() => handleGood(good + 1)}/>
      <Button text='neutral' handleClick={() => handleNeutral(neutral + 1)}/>
      <Button text='bad' handleClick={() => handleBad(bad + 1)}/>
      <Statistics good={good} bad={bad} neutral={neutral} average={average}/>
    </div>
  )
}

export default App
