import { useState } from 'react'

const Display = (props) => {
  return (
    <div>{props.counter}</div>
  )
}

const Button = (props) => {
  return (
    <div>
      {props.handleClick}
    </div>
  )
}

const App = () => {
  const [ counter, setCounter ] = useState(0)

  const increaseByOne = () => setCounter(counter + 1)
  const setToZero = () => setCounter(0)

  return (
    <div>

      <Display counter={counter}/>
      <Button title='plus' handleClick={increaseByOne} />
      <Button title='zero' handleClick={setToZero} />
    </div>
  )
}

export default App
