const Header = (props) => {
  return(
    <h1>
      {props.course}
    </h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part str={`${props.p1['name']} ${props.p1['exercises']}`} />
      <Part str={`${props.p2['name']} ${props.p2['exercises']}`} />
      <Part str={`${props.p3['name']} ${props.p3['exercises']}`} />

    </div>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.str}
    </p>
  )
}

const Total = (props) => {
  return (
    <p>Number of exercises {props.t1 + props.t2 + props.t3}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <>
      <Header course={course} />
      <Content p1={parts} p2={parts} p3={parts}/>
      <Total t1={parts} t2={parts} t3={parts}/>
    </>
  )
}

export default App
