import { useState } from 'react'

const Feedback = (props) => {

  return (
    <>
      <h1> give feedback</h1>
    </>
  )
}

const Statistics = () => {
  return (
    <>
      <h1>Statistics</h1>
    </>
  )
}

const StatisticLine = ({text,value}) => {

  return (
    <>
      <div>{text} {value}</div>
    </>
  )}

const Button = (props) => {

  return (
    <>
      <button onClick={props.handleClick}>{props.text}</button>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [average, setAverage] = useState(0)

  const handleGood = (newValue) => {
    setGood(newValue)
    setAverage((newValue-bad)/(newValue+bad+neutral))
  }
  const handleNeutral = (newValue) => {
    setNeutral(newValue)
    setAverage((good-bad)/(good+bad+newValue))
  }
  const handleBad = (newValue) => {
    setBad(newValue)
    setAverage((good-newValue)/(good+newValue+neutral))
  }



  return (
    <div>
      <h1>give feedback</h1>
      <Button text='good' handleClick={() => handleGood(good + 1)}/>
      <Button text='neutral' handleClick={() => handleNeutral(neutral + 1)}/>
      <Button text='bad' handleClick={() => handleBad(bad + 1)}/>
      <Statistics />
      <StatisticLine text="good" value ={good} />
      <StatisticLine text="neutral" value ={bad} />
      <StatisticLine text="bad" value ={neutral} />
      <StatisticLine text="average" value={average} />
      <StatisticLine text="positive" good={good} bad={bad} neutral={neutral} />
    </div>
  )
}

export default App
