import { useState } from 'react'

const Feedback = (props) => {

  return (
    <>
      <h1> give feedback</h1>
    </>
  )
}

const Statistics = (props) => {

  return (
    <>
      <h1>Statistics</h1>
    </>
  )
}

const Button = (props) => {

  return (
    <>
      <button onClick={props.handleClick}>{props.text}</button>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)
  const [average, setAverage] = useState(0)

  const handleGood = (newValue) => {
    setGood(newValue)
    calcAverage(good, bad, neutral)
    console.log(good)
    const updatedAll = calcAll(good, neutral, bad)
    setAll(updatedAll)
  }
  const handleNeutral = (newValue) => {
    setNeutral(newValue)
    calcAverage(good, bad, neutral)
    calcAll(good, neutral, bad)

  }
  const handleBad = (newValue) => {
    setBad(newValue)
    calcAverage(good, bad, neutral)
    calcAll(good, neutral, bad)
  }
  const calcAll = (g, n, b) => {
    const t = g+n+b
    setAll(t)
  }
  const calcAverage = (g, n, b) => {
    const m = (g-b) / (g+n+b)
    setAverage(m)
  }
  // const calcPositive = 

  return (
    <div>
      <h1>give feedback</h1>
      <Button text='good' handleClick={() => handleGood(good + 1)}/>
      <Button text='neutral' handleClick={() => handleNeutral(neutral + 1)}/>
      <Button text='bad' handleClick={() => handleBad(bad + 1)}/>
      <h1>Statistics</h1>
      <div>
        <div>good {good}</div>
        <div>neutral {neutral}</div>
        <div>bad {bad}</div>
        <div>all {all}</div>
        <div>average {average}</div>
        {/* <div>positive {positive}</div> */}
      </div>
    </div>
  )
}

export default App
